package com.heavygari.heavygaricustomer.base.utils;import java.util.Arrays;import java.util.Calendar;import java.util.List;import java.util.concurrent.TimeUnit;/** * Created by user on 24-Jan-18. */public class TimeAgo {    private static final List<Long> times = Arrays.asList(            TimeUnit.DAYS.toMillis(365),            TimeUnit.DAYS.toMillis(30),            TimeUnit.DAYS.toMillis(1),            TimeUnit.HOURS.toMillis(1),            TimeUnit.MINUTES.toMillis(1),            TimeUnit.SECONDS.toMillis(1));    private static final List<String> timesString = Arrays.asList("year", "month", "day", "hour", "minute", "second");    public static String toDuration(long duration) {        long  currentTime , differ;        if (duration == 0) {            return "";        }        currentTime = Calendar.getInstance().getTimeInMillis();        differ = currentTime - duration;        StringBuilder res = new StringBuilder();        for (int i = 0; i < TimeAgo.times.size(); i++) {            Long current = TimeAgo.times.get(i);            long temp = differ / current;            if (temp > 0) {                res.append(temp).append(" ").append(TimeAgo.timesString.get(i)).append(temp != 1 ? "s" : "").append(" ago");                break;            }        }        if ("".equals(res.toString()))            return "0 seconds ago";        else            return res.toString();    }}